import axios from "axios";

const ENDPOINT_URL ='http://localhost:3003/todo'

export const todoApi = {
    async getAll(){
        const result =await axios.get(ENDPOINT_URL);
        console.log(result)
        return result.data;
    },
    async post(todo){
        const result =await axios.get(ENDPOINT_URL,todo);
        return result.data;
    }
}

todoApi.post({
    id:3432432,
    content: 'test'
});
